package Loopingday1;

public class Example5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
           int i=3;
           int j=1;
           while(i<=20)
           {
        	   System.out.println(j+"*3="+i);
        	   i+=3;
        	   j++;
        	   
           }
	}

}
